import React, { useEffect, useRef } from 'react';
import $ from 'jquery';
import 'datatables.net';

function FlowerTable({ data}) {
  const tableRef = useRef(null);

  const table = $(tableRef.current).DataTable({
    bDestroy: true,
    columnDefs: [{
      defaultContent: '-',
      targets: '_all'
    }],
    columns: [
      { data: 'arrangement_id' },
      { data: 'name' },
      { data: 'description' },
      { data: 'quantity' },
      { data: 'price' },
      { data: 'url' },
      { data: 'category_id' },
      { data: 'flower_id' },
      {
        data: null,
        defaultContent: '<button class="btnEdit btn btn-primary">edit</button> <button class="btnDelete btn btn-danger">delete</button>',
        targets: -1
      }
    ],
    data: data
  });

 

  useEffect(() => {
    table.clear().rows.add(data).draw();
  }, [data, table]);

  return (
    <>
      <h1>Aranzmani</h1>
      <table className="table align-middle mb-0 bg-white">
  <thead className="bg-light">
    <tr>
      <th>Id</th>
      <th>Naziv</th>
      <th>Opis</th>
      <th>Kolicina</th>
      <th>Cena</th>
      <th>Slika</th>
      <th>Kategorija</th>
      <th>Cvet</th>
      <th>Odaberi</th>
    </tr>
  </thead>
  <tbody>
  {Array.isArray(data) && data.map((item) => (
  <tr key={item.arrangement_id}>
    <td>{item.arrangement_id}</td>
    <td>{item.name}</td>
    <td>{item.description}</td>
    <td>{item.quantity}</td>
    <td>{item.price}</td>
    <td>{item.url}</td>
    <td>{item.category_id}</td>
    <td>{item.flower_id}</td>
    <td>Odaberi</td>
  </tr>
))}

    
  </tbody>
</table>



    </>
  );
}

export default FlowerTable;
