import React from 'react'
import $ from 'jquery';
import 'datatables.net';

import { Component } from 'react';

function FlowerTable({ data, setData}) {


  

    useEffect(() => {
      $('#table').DataTable({
  
        "bDestroy": true,
        columnDefs: [{
          "defaultContent": "-",
          "targets": "_all"
        }], buttons: [
          'copy', 'excel', 'pdf'
        ],
        data: data,
        columns: [
          { "data": "id" },
          { "data": "naziv" },
          { "data": "opis" },
          { "data": "kolicina" },
          { "data": "cena" },
          { "data": "url" },
          { "data": "categoryid" },
          { "data": "flowerid" },
          
          { "data": null, defaultContent: "<button id='edit' class='btnEdit' variant='primary'   >edit</button> <button class='btnDelete'>delete</button>", targets: -1 }
          
        ]
      }
        
       
    );
    // $('#table .btnEdit').on('click', function () {
    //   console.log($('#table').DataTable().row($(this).closest('tr')));
    //   let z = $('#table').DataTable().row($(this).closest('tr')).data();
    // console.log(z);
    //   setPas(z);
    //   showAddPas();
    // });
  
    }, [data])
     
      
    
 
      
  
    return (
      <>
      <h1>Aranzmani</h1>
            <table id="table" className="table align-middle mb-0 bg-white">
    <thead className="bg-light">
      <tr>
      <th>Id</th>  
        <th>Naziv</th>
        <th>Opis</th>
        <th>Kolicina</th>
          <th>Cena</th>
          <th>Slika</th>
        <th>Kategorija</th>
          <th>Cvet</th>
          <th>Odaberi</th>
      </tr>
    </thead>
  
          
  
 
        </table>
      </>
    )
  }
export default FlowerTable
