import React, { Component } from 'react';
import axios from 'axios';

class ArrangementTable extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: []
    };
  }

  componentDidMount() {
    axios.get('api/arrangements')
      .then(response => {
        this.setState({ data: response.data });
        $(this.refs.table).DataTable({
          data: this.state.data,
          columns: [
            { title: 'Arrangement ID', data: 'arrangement_id' },
            { title: 'Name', data: 'name' },
            { title: 'Description', data: 'description' },
            { title: 'Quantity', data: 'quantity' },
            { title: 'Price', data: 'price' },
            { title: 'URL', data: 'url' },
            { title: 'Category ID', data: 'category_id' },
            { title: 'Flower ID', data: 'flower_id' }
          ]
        });
      })
      .catch(error => {
        console.log(error);
      });
  }
  
  render() {
    return (
      <table className="table table-striped" ref="table">
        <thead>
          <tr>
            <th>Arrangement ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>URL</th>
            <th>Category ID</th>
            <th>Flower ID</th>
          </tr>
        </thead>
        <tbody>
        </tbody>
      </table>
    );
  }
}  

export default ArrangementTable;
